p_rec.program_details['eligibility'] = 'Must be age 11-18 residing in Alameda County or residing anywhere with an open Alameda County Social Services or Probation case.'
p_rec.program_details['cost'] = 'None.'
p_rec.program_details['eir_id'] = 'PG003247'
p_rec.address = '436 - 14th Street, Suite 1201, Oakland'
